mapper.select:
SELECT t1.time,t1.id "latticeId",t1.sid "sid",spk,onebest,msgXi,reliability,t1.time "startTime", date_add(t1.time,INTERVAL ((t2.ed - t2.bg)/1000) SECOND)"endTime" FROM feq_lattice t1 LEFT JOIN feiq_record t2 ON t1.rcd_id = t2.id WHERE 1 = 1
and t1.time >= #{startTime}
and t1.time <= #{endTime}
and t1.sid = #{sid}

select id,sid,reliability,start_time "startTime",end_time "endTime" from phys_aberration where 1 = 1

     and sid = #{sid}


     and reliability > #{reliability}

   order by id
